sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

	$rootScope.$on('selectNote', function(event, data) {
		console.log('Listener caught NoteID: ' + $rootScope.noteID);
		c.server.get({
			action: 'getNote',           /* action name to be identified in server script (via "input.action" check) */
			noteID: $rootScope.noteID    /* "noteID" field - emitted to the "$rootScope" by "Notes List" widget */
		}).then(function(srv) {
			// -----------------------------------------------------
			// server data ("srv") delivered by the server script
			// - field values are stored in object array "data.note"
			// -----------------------------------------------------
			c.data.title  = srv.data.note.title;
			c.data.note   = srv.data.note.note;
			c.data.noteID = srv.data.note.sys_id;
		});

	});

	c.updateNote = function(updateType) {
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			noteBody: c.data.note,
			noteTitle: c.data.title
		}).then (function(srv) {
			// ---------------------------------------------
			// When the promise is returned, emit the change
			// ---------------------------------------------
			if (updateType == 'title' || updateType == 'body') {
				$rootScope.$emit('updateTitle', srv.data);
			}
		});
	}

};

sp_widget.script:
(function() {
	/* populate the 'data' object */

	if (input && input.noteID) {
		var note_table = new GlideRecord('x_snc_createnotes_note');
		if (note_table.get(input.noteID)) {
			if (input.action == 'getNote') {
				data.note = {};
				$sp.getRecordValues(data.note, note_table, "title, note, sys_id");
			} else if (input.action == "updateNote") {
				note_table.title = input.noteTitle;
				note_table.note = input.noteBody;
				note_table.update();
				// --------------------------------------------------------------
				// Need to update the data object with the new title
				// and first 20 characters of the note (do not want to write huge
				// strings to the Notes List widget)
				// --------------------------------------------------------------
				data.title = note_table.getValue('title');
				data.note = note_table.getValue('note').slice(0,20);
			}
		}
	}

})();
